import React from 'react'
import '../activity/activity.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faTrash } from "@fortawesome/free-solid-svg-icons";
import ToggleButton from '@mui/material/ToggleButton';
import ToggleButtonGroup from '@mui/material/ToggleButtonGroup';
import TextField from '@mui/material/TextField';
import Table from '../table/Table';
import TimePicker from 'react-time-picker';
import { Select } from '@mui/material';
import MenuItem from '@mui/material/MenuItem';
import { engineersNames } from '../../utils/employeeList';

let initState = {
    discipline: "elec",
    scope: "ESP"
}

let reducer = (state, action) => {
    switch (action.type) {
        case 'setDiscipline':
            return {...state, discipline: action.setDiscipline }
        case 'setScope':
            return {...state, scope: action.setDiscipline }

        default:
            throw new Error();

    }
}

export default function Activity() {

    const [state , dispatch] = React.useReducer (reducer, initState);

    // let [discipline , setDiscipline] = React.useState ('elec');
    // let [scope , setScope] = React.useState ('esp');
    let [description ,setDescription] = React.useState ('');
    let [activity , setActivity] = React.useState ([]);
    let [timeStamp ,setTimeStamp] = React.useState ('09:00');
    let [person ,setPerson] = React.useState ('eldessouki');

    let responsibles = engineersNames.contacts.map (
        contact => contact.Name
    );
        
    let setActivityHandler = () => {
        setActivity (
            [
                ...activity,
                {
                    description: description,
                    disciplines: state.discipline,
                    scope: scope,
                    Responsibility: person,
                    timing: timeStamp,
                }
            ]
        )
    }
    
  return (
    <div className='activity__container'>
        <div className='input__field'>
            {/*Toggle controller starts here */}  
            <div className='toggle__controllers'>
                <ToggleButtonGroup
                    color= 'warning'
                    value={state.discipline}
                    exclusive
                    onChange={
                        (e) => {
                            dispatch ({type: "setDiscipline", setDiscipline: e.target.value})
                        }                      
                    }
                >
                    <ToggleButton value="elec">Elec</ToggleButton>
                    <ToggleButton value="I&C">I&C</ToggleButton>
                    <ToggleButton value="mech">Mech</ToggleButton>
                </ToggleButtonGroup>

                <ToggleButtonGroup
                    color= 'primary'
                    value={state.scope}
                    exclusive
                    onChange={
                        (e)=> {
                            dispatch ({type: "setScope", setDiscipline: e.target.value})                        
                    }
                }
                >
                    <ToggleButton value="sag">SAG</ToggleButton>
                    <ToggleButton value="esp">ESP</ToggleButton>
                </ToggleButtonGroup>
            </div>

            {/*User text filed starts here */}  
            <TextField
            color= 'primary'
            fullWidth = {true}
            id="outlined-multiline-static"
            label={`${scope}/${state.discipline}`}
            multiline
            rows={3}
            defaultValue= {'Include the activity here.....'}
            onChange= {
                (e) => {
                    setDescription (
                        e.target.value
                    )
                }
            }
            />
            <div className='control__icons'>
                <FontAwesomeIcon icon= {faPlus} onClick= {setActivityHandler} />
                <FontAwesomeIcon icon= {faTrash} />
            </div>

        </div>

        {/*Time picker section starts here */}
        <div className="activity__stackHolders">
        <TimePicker 
        onChange={
            (value) => {
                setTimeStamp (value)
            }
        }
        value = {timeStamp}
        />

        {/*Responsibility selector drop down menu */}
        <Select
          value={person}
          onChange={(e) => {
            setPerson (e.target.value)
          }}
        >
          
          {
            responsibles.map (
                (responsible,i) => (
                    <MenuItem key= {i} value={responsible}>{responsible}</MenuItem>
                )
            )

          }
        </Select>

        </div>

        {/*Data table component section starts here */}
        <Table data = {activity} />
    </div>
  )
}
