import React from 'react'
import '../activity/activity.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faTrash } from "@fortawesome/free-solid-svg-icons";
import ToggleButton from '@mui/material/ToggleButton';
import ToggleButtonGroup from '@mui/material/ToggleButtonGroup';
import TextField from '@mui/material/TextField';
import Table from '../table/Table';
import Timepicker, { TimeContext } from '../Timepicker/Timepicker';


export default function Activity() {
    let [discipline , setDiscipline] = React.useState ('elec');
    let [scope , setScope] = React.useState ('esp');
    let [description ,setDescription] = React.useState ('');
    let [activity , setActivity] = React.useState ([]);

    let timeStamp = React.useContext(TimeContext);

    
    let setActivityHandler = () => {
        setActivity (
            [
                ...activity,
                {
                    description: description,
                    disciplines: discipline,
                    scope: scope,
                    Responsibility: 'shaaban',
                    timing: timeStamp
                }
            ]
        )
    }

  return (
    <div className='activity__container'>
        <div className='input__field'>
            <div className='toggle__controllers'>
                <ToggleButtonGroup
                    color= 'warning'
                    value={discipline}
                    exclusive
                    onChange={(e)=> {
                        setDiscipline(e.target.value)
                    }}
                >
                    <ToggleButton value="elec">Elec</ToggleButton>
                    <ToggleButton value="I&C">I&C</ToggleButton>
                    <ToggleButton value="mech">Mech</ToggleButton>
                </ToggleButtonGroup>

                <ToggleButtonGroup
                    color= 'primary'
                    value={scope}
                    exclusive
                    onChange={(e)=> {
                        setScope(e.target.value)
                    }}
                >
                    <ToggleButton value="sag">SAG</ToggleButton>
                    <ToggleButton value="esp">ESP</ToggleButton>
                </ToggleButtonGroup>
            </div>

            <TextField
            color= 'primary'
            fullWidth = {true}
            id="outlined-multiline-static"
            label={`${scope}/${discipline}`}
            multiline
            rows={3}
            defaultValue= {'Include the activity here.....'}
            onChange= {
                (e) => {
                    setDescription (
                        e.target.value
                    )
                }
            }
            />
            <div className='control__icons'>
                <FontAwesomeIcon icon= {faPlus} onClick= {setActivityHandler} />
                <FontAwesomeIcon icon= {faTrash} />

                
            </div>

        </div>

        <Timepicker />
        <Table data = {activity} />


    </div>
  )
}
