import React from 'react';
import '../Popup/Popup.scss'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEllipsisVertical, faTrash, faCircleCheck, faEdit } from "@fortawesome/free-solid-svg-icons";



export default function Popup () { 
    return (
        <Menu>
            <Navitems />
        </Menu>
        
    )
}

function Menu (props) {
    let [toggle , setToggle] = React.useState (false);
    
    console.log(toggle);
    return (
        <div className='menu__navitems'>
            {
                 <FontAwesomeIcon onClick={() => setToggle (!toggle)} className= "menu__navMenu" icon= {faEllipsisVertical}/>
            }
          <ul className='menu__navitems__icons'> 
            {
                toggle && props.children 
            }
          </ul>
        </div>
        
    )
}

function Navitems () {
    return (
        <>
            <li>
                <FontAwesomeIcon className= "menu__icon" icon = {faTrash} />
            </li>
            <li>
                <FontAwesomeIcon className= "menu__icon" icon = {faCircleCheck} />
            </li>
            <li>
                <FontAwesomeIcon className= "menu__icon" icon = {faEdit} />
            </li>
        </>
        
    )
}
